import React, { Component } from "react";
import "bootstrap/dist/css/bootstrap.min.css";
import axios from "axios";
import "../component/css/AdminDashboard.css"
import { Link } from "react-router-dom";
import { Redirect } from "react-router-dom";
import Streams from "../component/streams";
import BooksAdmin from "../component/BooksAdmin";


const Exercise = props => (
    <tr className="col">
        <td>
            <img height="100px" width="100px" src={"http://localhost:5000/exercise/image/:" + (props.file).substring(15)} alt={(props.file).substring(15)} />
            <a href={"http://localhost:5000/exercise/pdf/:" + (props.pdfFile).substring(15)} rel="noopener noreferrer" target="_blank" >Link</a>
        </td>
        <td className="tbody-light">{props.exercise.username}</td>
        <td className="tbody-light">{props.exercise.description}</td>
        <td className="tbody-light">{props.exercise.duration}</td>
        <td className="tbody-light">{(props.exercise.date).substring(0, 10)}</td>
        {/* <td className="tbody-light">{props.exercise.path} */}
        {/* </td> */}
        <td className="tbody-light">
            <Link to={"/EditExercise/" + props.exercise._id}>
                <button style={{ margin: "5px" }} type="button" className="btn btn-primary btn-md">Edit</button>
            </Link>
        </td>
        <td>
            <Link to="/" className="button-small" onClick={() => props.deleteExercise(props.id)}>
                <button type="button" className="btn btn-primary btn-md"> Delete </button>
            </Link>
        </td>
    </tr>
);
const StreamElementCreator = props => (
    <tr className="col">
        <td className="tbody-light">{props.username}</td>
        <td className="tbody-light">
            <Link to={""}>
                <button style={{ margin: "5px" }} type="button" className="btn btn-primary btn-md">Delete</button>
            </Link>
        </td>

    </tr>
);
export default class AdminDashboard extends Component {
    constructor(props) {
        super(props);
        this.deleteExcercise = this.deleteExcercise.bind(this);
        this.ExerciseFunction = this.ExerciseFunction.bind(this);
        this.StreamsFunction = this.StreamsFunction.bind(this);
        this.componentDidMount = this.componentDidMount.bind(this);
        this.Refresh = this.Refresh.bind(this);
        this.SideMenubar = this.SideMenubar.bind(this);
        const token = localStorage.getItem("token");
        let LoggedIn = true;
        if (token === null) {
            LoggedIn = false;
        }
        this.state = {
            exercises: [],
            exerciseDiv: false,
            StreamDiv: false,
            streams: [],
            LoggedIn
        }
    }

    StreamsFunction() {
        this.setState({
            StreamDiv: true,
            exerciseDiv: false
        })
    }
    ExerciseFunction() {
        console.log("button is");
        this.setState({
            exerciseDiv: true,
            StreamDiv: false
        });
    }
    
    Refresh() {
        window.location.reload("http://localhost:3000/AdminDashboard");
    }
    SideMenubar(){
        return (
            <div className="col-2 borderOfOptions sidenav">
                        <ul className="ul-padding-none">
                            <Link className="LinksForOptions" onClick={this.ExerciseFunction}>
                                <li>streams</li>
                            </Link>
                            <Link className="LinksForOptions" onClick={this.StreamsFunction}>
                                <li>Books</li>
                            </Link>
                            <Link className="LinksForOptions" onClick={""}>
                                <li>Feedback</li>
                            </Link>
                            <Link className="LinksForOptions" onClick={this.Refresh}>
                                <li>Refresh Page</li>
                            </Link>
                            <Link className="LinksForOptions" to="/Logout">
                                <li><button className="btn btn-dark btn-block ">Logout</button></li>
                            </Link>
                        </ul>
                    </div>
        )
    }
    render() {
        if (this.state.LoggedIn === false) {
            return <Redirect to="/" />
        }
        if (this.state.exerciseDiv) {
            return (
                <div>
                    <Streams />
                    {this.SideMenubar()}
                </div>
            )
        }
        if (this.state.StreamDiv) {
            return (
                <div>
                    <BooksAdmin />
                    {this.SideMenubar()}
                </div>
            );
        }
        return (
            <div className="container-fluid">
                <div className="row heading-div">
                    <div>
                        <h2 className="headingBrand">
                            Notebook
                    </h2>
                    </div>
                </div>
                <div className="row">
                    <div className="col-10">
                        <h1>
                            Wellcome Admin
                    </h1>
                    </div>
                </div>
                {this.SideMenubar()}
            </div>
        )
    }
}
