import React, { Component } from "react";
import DatePicker from "react-datepicker";

export default class CreateExercise extends Component {
    constructor(props) {
        super(props);
        this.state = {
            username: "",
            description: "",
            duration: 0,
            data: new Date(),
            users: []
        }
    }
    componentDidMount() {
        this.setState({
            user: ["test user"],
            username: "test user"
        })
    }
    onChangeUsername(event) {
        const username = event.target.value;
        this.setState({
            username: username,
        })
    }
    onChangeDescription(event) {
        const description = event.target.value;
        this.setState({
            description: description,
        })
    }
    onChangeDuration(event) {
        const duration = event.target.value;
        this.setState({
            duration: duration,
        })
    }
    onChangeDate(event) {
        const date = event.target.value;
        this.setState({
            date: date,
        })
    }
    onSubmit(event) {
        event.preventDefault();
        const exercise = {
            username: this.state.username,
            description: this.state.description,
            duration: this.state.duration,
            date: this.state.date,
        }
        console.log(exercise);
        window.location("/");
    }
    render() {
        return (
            <div>
                <h1>this is a CreateExercise Page</h1>
                <form onSubmit={this.onSubmit}>
                    <div class="form-group">
                        <label for="exampleFormControlInput1">user name</label>
                        <select id="inputState" onChange={this.onChangeUsername} class="form-control">
                            {
                                this.user.map(user => {
                                    return <option key={user} value={user}>{user}

                                    </option>
                                })
                            }
                        </select>
                        <label for="exampleFormControlInput1">description</label>
                        <textarea class="form-control" onChange={this.onChangeDescription} id="exampleFormControlTextarea1" rows="3"></textarea>
                        <label for="exampleFormControlInput1" onChange={this.onChangeDuration}>duration</label>
                        <input type="text" class="form-control" id="exampleFormControlInput1" />
                        <label for="exampleFormControlInput1" onChange={this.onChangeDate}>Date</label>
                        <input type="date" class="form-control" id="exampleFormControlInput1" />
                        <button type="submit" class="btn btn-primary mb-2">submit</button>
                    </div>
                </form>
            </div>
        )
    }
}
