    //     })
    // }
    render() {
        return (
            <div>
                <h1>LoggedExercise</h1>
                <table className="table table-bordered table-hover">
                    <thead className="thead-dark">
                        <tr>
                            <th>Username</th>
                            <th>Description</th>
                            <th>Duration</th>
                            <th>Date</th>
                            <th>Action</th>
                        </tr>
                    </thead>
                    <tbody>
                        <Exercise />
                    </tbody>
                </table>
            </div>
        )
    }
}
