import React, { Component } from "react";
import axios from "axios";
export default class CreateExercise extends Component {
    constructor(props) {
        super(props);
        this.onChangeUsername = this.onChangeUsername.bind(this);
        this.onChangeDescription = this.onChangeDescription.bind(this);
        this.onChangeDuration = this.onChangeDuration.bind(this);
        this.onChangeDate = this.onChangeDate.bind(this);
        this.onSubmitForm = this.onSubmitForm.bind(this);
        this.state = {
            username: "",
            description: "",
            duration: 0,
            file:null,
            data: new Date(),
            users: []
        }
    }
    componentDidMount() {
        axios.get("http://localhost:5000/user/").then(res => {
            if (res.data.length > 0) {
                this.setState({
                    users:res.data.map(val => val.username),
                    username:res.data[0].username
                })
            }
        })
    }
    onChangeUsername(event) {
        const username = event.target.value;
        this.setState({
            username: username,
        })
    }
    onChangeDescription(event) {
        const description = event.target.value;
        this.setState({
            description: description,
        })
    }
    onChangeDuration(event) {
        const duration = event.target.value;
        this.setState({
            duration: duration,
        })
    }
    onChangeDate(event) {
        const date = event.target.value;
        this.setState({
            date: date,
        })
    }
    fileChangedHandler = (event) => {
        const file = event.target.files[0];
        this.setState({ file: file });
    }
      
    onSubmitForm(event) {
        event.preventDefault();
        // const exercise = {
        //     username: this.state.username,
        //     description: this.state.description,
        //     duration: this.state.duration,
        //     date: this.state.date,
        //     file:this.state.file
        // }
        console.log(this.state);
        let formData = new FormData();
        formData.append('username', event.username.value);

        // formData.append('username', this.state.username);
        // formData.append('description', this.state.description);
        // formData.append('duration', this.state.duration);
        // formData.append('date', this.state.date);
        // formData.append('fileName', this.state.file);
        console.log(formData);
        // axios.post("http://localhost:5000/exercise/add", formData).then(res => console.log(res.data));
        // window.location = "/";
    }
    render() {
        return (
            <form onSubmit={this.onSubmitForm} encType="multipart/form-data">
                <div className="form-group">
                    <label htmlFor="exampleFormControlInput1">user name</label>
                    <select id="inputState" name="username" onChange={this.onChangeUsername} className="form-control">
                        {
                            this.state.users.map(user => {
                                return <option key={user} value={user}>{user}

                                </option>
                            })
                        }
                    </select>
                    <label htmlFor="exampleFormControlInput1">description</label>
                    <textarea className="form-control" name="description" value={this.state.description} onChange={this.onChangeDescription} id="exampleFormControlTextarea1" rows="3"></textarea>
                    <label htmlFor="exampleFormControlInput1" >duration</label>
                    <input type="text" className="form-control" name="duration" value={this.state.duration} onChange={this.onChangeDuration} id="exampleFormControlInput1" />
                    <label htmlFor="exampleFormControlInput1" >Pick Date Below</label>
                    <input type="date" className="form-control" name="date" onChange={this.onChangeDate} value={this.state.date} />
                    <label htmlFor="exampleFormControlInput1" >Pick File</label>
                    <input type="file" className="form-control" name="file" onChange={this.fileChangedHandler}/>
                    <button style={{ "marginTop": "10px" }} type="submit" className="btn btn-primary mb-2">submit</button>
                </div>
            </form>
        )
    }
}
