import React, { Component } from "react";

export default class CreateUSer extends Component {
    constructor(props){
        super(props);
        this.onChangeUsername = this.onChangeUsername.bind(this);
        this.onSubmitForm = this.onSubmitForm.bind(this);
        this.state={
            username:""
        }
    }
    onChangeUsername(event){
        const username=event.target.value;
        this.setState({
            username:username
        });
    }
    onSubmitForm(event){
        event.preventDefault();
        const userBackup = this.state.user;
        this.setState({
            username:""
        })
        console.log(userBackup);
    }
    render() {
        return (
            <div>
                <h1>create user</h1>
                <form onSubmit={this.onSubmitForm}>
                    <div className="form-group">
                        <label htmlsfor="exampleFormControlInput1">user name</label>
                        <input type="text" className="form-control" onChange={this.onChangeUsername} value={this.state.user} />
                        </div>
                        <div className="form-group">
                            <button style={{ "marginTop": "10px" }} type="submit" className="btn btn-primary mb-2">submit</button>
                        </div>
                </form>
            </div>

        )
    }
}
