import React, { Component } from "react";
import "bootstrap/dist/css/bootstrap.min.css";
import "./css/books.css";
import { Link } from "react-router-dom";
import axios from "axios";

const BookForDownload = props => {
    return (
        <div class="card col-div-books-category" style={{ width: "18rem" }}>
            <img class="card-img-top" src={"http://localhost:5000/exercise/image/:"+(props.file).substring(15)} alt="Card image cap" />            
            <div class="card-body">
                <h5 class="card-title">{props.name}</h5>
                    <p class="card-text">{props.description}.</p>
            </div>
            <div class="card-body">
            <Link to="#" class="card-link">
                <button className="btn btn-primary">Download</button>
            </Link>
            </div>
        </div>
    )
}

export default class books extends Component {
    constructor(props) {
        super(props);
        this.componentDidMount = this.componentDidMount.bind(this)
        this.BooksCardComponentMaker = this.BooksCardComponentMaker.bind(this);
        this.state = {
            data: [],
        }
    }
    componentDidMount() {
        axios.get("http://localhost:5000/exercise/").then(res => {
            console.log(this.props.match.params.name);
            this.setState({
                data: res.data.users
            })
        });
    }
    BooksCardComponentMaker() {
        console.log(this.state.data);
        return this.state.data.map(currentExercise => {
            return <BookForDownload file={currentExercise.path} pdfFile={currentExercise.pathPdf} key={currentExercise._id} id={currentExercise._id} exercise={currentExercise}  name={currentExercise.username} description={currentExercise.description}/>
        })
    }
    render() {
        return (
            <div className="row row-div-Books-category">
                {this.BooksCardComponentMaker()}
                
            </div>

        )

    }
}
