import React, { Component } from "react";
// import { Link } from "react-router-dom";
import axios from "axios";
const Exercise = props => (
    <tr>
        <td>
            hello
        </td>
    </tr>
    // <tr>
    //     <td className="tbody-light">{props.exercise.username}</td>
