    //         <a href="#" onClick={props.deleteExcercise(props.exercise._id)}>Delete</a>
    //     </td>
    // </tr>
);
export default class ExerciseList extends Component {
    constructor(props) {
        super(props);
        // this.deleteExcercise = this.deleteExcercise.bind(this);
        this.state = {
            username: "",
            description: "",
            duration: 0,
            data: new Date(),
            users: []
        }
    }
    componentDidMount() {
        axios.get("http://localhost:5000/user/").then(res => {
            if (res.data.length > 0) {
                this.setState({
                    users:res.data.map(val => val.username),
                    username:res.data[0].username
                })
            }
        })
        console.log(this.state);
    }
    // deleteExcercise(id) {
    //     axios.delete("http://localhost:5000/exercise/" + id).then(res => console.log(res.data));
    //     this.setState({
    //         exercises: this.state.exercises.filter(el => el._id !== id)
    //     });
    // }
    // exerciseList() {
    //     return this.state.exercises.map(currentExercise => {
    //         return <Exercise key={currentExercise._id} exercise={currentExercise} deleteExercise={this.deleteExcercise} />
    //     })
    // }
    render() {
        return (
            <div>
                <h1>LoggedExercise</h1>
                <table className="table table-bordered table-hover">
                    <thead className="thead-dark">
                        <tr>
                            <th>Username</th>
                            <th>Description</th>
                            <th>Duration</th>
                            <th>Date</th>
                            <th>Action</th>
                        </tr>
                    </thead>
                    <tbody>
                        <Exercise />
                    </tbody>
                </table>
            </div>
        )
    }
}
