import React, { Component } from "react";
import "bootstrap/dist/css/bootstrap.min.css";
import axios from "axios";
import "./css/AdminLogin.css";
export default class AdminLogin extends Component {
  constructor(props) {
    super(props);
    this.state = {
      email: "",
      pass: ""
    }
    this.onChangePassword = this.onChangePassword.bind(this);
    this.HandleFormSubmission = this.HandleFormSubmission.bind(this);
    this.AdminDashBoard = this.AdminDashBoard.bind(this);
    this.onChangeEmail = this.onChangeEmail.bind(this);
  }
  onChangePassword(event) {
    console.log(event.target.value);
    const passWord = event.target.value;
    this.setState({ pass: passWord });
  }
  onChangeEmail(event) {
    console.log(event.target.value);
    const Email = event.target.value;
    this.setState({ email: Email });
  }
  HandleFormSubmission(event) {
    event.preventDefault();
    console.log("button is clicked.");
    const password = this.state.pass;
    console.log(password);
    axios
      .post("http://localhost:5000/password", { passToPost: password })
      .then(res => this.AdminDashBoard(res))
      .catch(err => console.log(err));
  }
  AdminDashBoard(res) {
    console.log(res.data.authentication);
    if (res.data.authentication === true) {
    }

  }
  render() {
    return (
      <div className="container-fluid-div-admin-login">
        <div className="row row-margin">
          <div className="lg-col-7 md-col-7 sm-col-10color-admin-welcome">
            <h1 className="welcome-heading">
              Admin Please Login here!
            </h1>
            <div className="form-div">
            <form onSubmit={this.HandleFormSubmission}>
              <div class="form-group">
                <label for="exampleInputEmail1">Email address</label>
                <input type="email" class="form-control" onChange={this.onChangeEmail} id="exampleInputEmail1" aria-describedby="emailHelp" placeholder="Enter email" />
                <small id="emailHelp" class="form-text text-muted">We'll never share your email with anyone else.</small>
              </div>
              <div class="form-group">
                <label for="exampleInputPassword1">Password</label>
                <input type="password" onChange={this.onChangePassword} class="form-control" id="exampleInputPassword1" placeholder="Password" />
              </div>
              <button type="submit" class="btn btn-dark">Submit</button>
            </form>
            </div>
          </div>
        </div>
      </div>
    )
  }
}
