import React, { Component } from "react";
import "bootstrap/dist/css/bootstrap.min.css";
import axios from "axios";
import "../component/css/AdminDashboard.css"
import { Link } from "react-router-dom";

const StreamElementCreator = props => (
    <tr className="col">
        <td className="tbody-light">{props.username}</td>
        <td className="tbody-light">
            <Link to={""}>
                <button style={{ margin: "5px" }} type="button" className="btn btn-primary btn-md">Delete</button>
            </Link>
        </td>

    </tr>
);
export default class Streams extends Component {
    constructor(props) {
        super(props);
        this.deleteExcercise = this.deleteExcercise.bind(this);
        this.Hello = this.Hello.bind(this);
        this.hello = this.hello.bind(this);
        this.componentDidMount = this.componentDidMount.bind(this);
        this.Refresh= this.Refresh.bind(this);
        const token = localStorage.getItem("token");
        let LoggedIn = true;
        if(token === null){
            LoggedIn = false;
        }
        this.state = {
            exercises: [],
            exerciseDiv: false,
            StreamDiv: false,
            streams: [],
            LoggedIn
        }
    }
    componentDidMount() {
        axios.get("http://localhost:5000/user/").then(res => {
            console.log(res);
            this.setState({
                streams: res.data,
            });
        }).catch(err => console.log(err));
    }

    // deleteExcercise(id) {
    //     axios.delete("http://localhost:5000/exercise/" + id).then(res => console.log(res.data));
    //     this.setState({
    //         exercises: this.state.exercises.filter(el => el._id !== id)
    //     });
    //     console.log(this.state);
    // }
    
    
    Streams() {
        console.log(this.state.streams);
        return this.state.streams.map(currentExercise => {
            return <StreamElementCreator username={currentExercise.username} key={currentExercise._id} id={currentExercise._id} deletestream={this.deleteExcercise} />
        });
    }
    Refresh(){
        window.location.reload("http://localhost:3000/AdminDashboard");
    }
    render(){
        return(
            <div className="container-fluid">
                    <div className="row heading-div">
                        <div>
                            <h1 className="headingBrand">
                                Notebook
                    </h1>
                        </div>
                    </div>
                    <div className="row">
                        <div className="table-responsive-xl col-10" id="list">
                            <table style={{ textAlign: "center" }} className="table table-bordered table-striped">
                                <thead className="thead-dark">
                                    <tr>
                                        <th>Streams</th>
                                        <th colSpan="2" >Action</th>
                                    </tr>
                                </thead>
                                <tbody>
                                    {this.Streams()}
                                </tbody>
                            </table>
                        </div>
                        <div className="col-2 borderOfOptions sidenav">
                            <ul className="ul-padding-none">
                                <Link className="LinksForOptions" onClick={this.Hello}>
                                    <li>streams</li>
                                </Link>
                                <Link className="LinksForOptions" onClick={this.hello}>
                                    <li>Books</li>
                                </Link>
                                <Link className="LinksForOptions" onClick={""}>
                                    <li>Feedback</li>
                                </Link>
                                <Link className="LinksForOptions" onClick={this.Refresh}>
                                    <li>Refresh Page</li>
                                </Link>
                                <Link className="LinksForOptions" to="/Logout">
                                <li><button className="btn btn-dark btn-block ">Logout</button></li>
                                </Link>
                            </ul>
                        </div>
                    </div>
                </div>
        )
    }
}
