import React, { Component } from "react";
import "bootstrap/dist/css/bootstrap.min.css";
import ReactDom from "react-dom";
import "./css/books.css";
import axios from "axios";
import { Button, Modal } from 'react-bootstrap';


function MyVerticallyCenteredModal(props) {
    const [pass, setPass] = React.useState("");
    function  onChangePassword(event){
        console.log(event.target.value);
        const passWord = event.target.value;
        setPass(passWord);
    }

    return (
      <Modal
        {...props}
        size="lg"
        aria-labelledby="contained-modal-title-vcenter"
        centered
      >
        <Modal.Header closeButton>
          <Modal.Title id="contained-modal-title-vcenter">
            Please enter password provided by developer.
          </Modal.Title>
        </Modal.Header>
        <Modal.Body>
          <h4>Enter password</h4>
          <form method="post">
            <div class="form-group">
                <p>{pass}</p>
              <input type="password" onChange={onChangePassword} class="form-control" name="pass"  min="8"/>
            </div>
            <button className="btn btn-dark" type="submit" name="AdminLoginButton">Submit</button>
          </form>
        </Modal.Body>
        <Modal.Footer>
          <Button  className="btn btn-dark" onClick={props.onHide} >Close</Button>
        </Modal.Footer>
      </Modal>
    );
  }
  
  function AdminLogin(){
    const [modalShow, setModalShow] = React.useState(false);
  
    return (
        <>
        <Button variant="dark" onClick={() => setModalShow(true)}>
          Admin
        </Button>
        <MyVerticallyCenteredModal
          show={modalShow}
          onHide={() => setModalShow(false)}
        />
      </>
    );
}
export default AdminLogin;
