import React, { Component } from "react";
import "bootstrap/dist/css/bootstrap.min.css";
import axios from "axios";
import "./AdminComponentsCss/AdminDashboard.css"
import { Link } from "react-router-dom";
import { Redirect } from "react-router-dom";
import Streams from "../AdminComponents/streams";
import FeedbackAdmin from "../AdminComponents/FeedbackAdmin";

import BooksAdmin from "../AdminComponents/BooksAdmin";
import CreateUser from "../AdminComponents/CreateUser";
import CreateExercise from "../AdminComponents/CreateExercise";

import { Beforeunload } from 'react-beforeunload';

export default class AdminDashboard extends Component {
    constructor(props) {
        super(props);
        this.ExerciseFunction = this.ExerciseFunction.bind(this);
        this.StreamsFunction = this.StreamsFunction.bind(this);
        this.SideMenubar = this.SideMenubar.bind(this);
        this.FeedbackDivFunction = this.FeedbackDivFunction.bind(this);
        this.CreateStreamsFunction = this.CreateStreamsFunction.bind(this);
        this.UploadBookFunction = this.UploadBookFunction.bind(this);
        const token = localStorage.getItem("token");
        let LoggedIn = true;
        if (token === null) {
            LoggedIn = false;
        }
        this.state = {
            exerciseDiv: false,
            StreamDiv: false,
            FeedbackDiv:false,
            UploadBooks:false,
            CreateStreamDiv:false,
            LoggedIn
        }
    }
    StreamsFunction() {
        this.setState({
            StreamDiv: true,
            exerciseDiv: false,
            FeedbackDiv:false,
            UploadBooks:false,
            CreateStreamDiv:false,
        })
    }
    CreateStreamsFunction(){
        this.setState({
            exerciseDiv: false,
            StreamDiv: false,
            FeedbackDiv:false,
            UploadBooks:false,
            CreateStreamDiv:true,
        });
    }
    UploadBookFunction(){
        this.setState({
            exerciseDiv: false,
            StreamDiv: false,
            FeedbackDiv:false,
            UploadBooks:true,
            CreateStreamDiv:false,
        });
    }
    ExerciseFunction() {
        this.setState({
            exerciseDiv: true,
            StreamDiv: false,
            FeedbackDiv:false,
            UploadBooks:false,
            CreateStreamDiv:false,
        });
    }
    FeedbackDivFunction() {
        this.setState({
            FeedbackDiv:true,
            exerciseDiv: false,
            StreamDiv: false,
            UploadBooks:false,
            CreateStreamDiv:false,
        });
    }
    SideMenubar(){
        return (
            <nav class="navbar navbar-expand-lg navbar-light bg-light">
  <button class="navbar-toggler" type="button" data-toggle="collapse" data-target="#navbarTogglerDemo03" aria-controls="navbarTogglerDemo03" aria-expanded="false" aria-label="Toggle navigation">
    <span class="navbar-toggler-icon"></span>
  </button>
  <div class="collapse navbar-collapse" id="navbarTogglerDemo03">

            <div className="col-2 borderOfOptions sidenav">
                        <ul className="ul-padding-none">
                            <Link className="LinksForOptions" onClick={this.ExerciseFunction}>
                                <li>streams</li>
                            </Link>
                            <Link className="LinksForOptions" onClick={this.StreamsFunction}>
                                <li>Books</li>
                            </Link>
                            <Link className="LinksForOptions" onClick={this.FeedbackDivFunction}>
                                <li>Feedback</li>
                            </Link>
                            <Link className="LinksForOptions" onClick={this.CreateStreamsFunction}>
                                <li>Create Stream</li>
                            </Link>
                            <Link className="LinksForOptions" onClick={this.UploadBookFunction}>
                                <li>upload Books</li>
                            </Link>
                            <Link className="LinksForOptions" to="/Logout">
                                <li><button className="btn btn-dark btn-block ">Logout</button></li>
                            </Link>
                        </ul>
                    </div>
                    </div>
                    </nav>
        )
    }
    render() {
        if (this.state.LoggedIn === false) {
            return  <Redirect to="/" />
        }
        if(this.state.CreateStreamDiv){
            return <div>
                        <Beforeunload onBeforeunload={()=>localStorage.removeItem("token")}>
                        {this.SideMenubar()}
                            <CreateUser />
                        </Beforeunload>
                    </div>
        }
        
        if(this.state.UploadBooks){
            return <div>
                        <Beforeunload onBeforeunload={()=>localStorage.removeItem("token")}>
                            <CreateExercise />
                             {this.SideMenubar()}
                        </Beforeunload>
                    </div>
        }
        if (this.state.exerciseDiv) {
            return (
                <div>
                  <Beforeunload onBeforeunload={()=>localStorage.removeItem("token")}>
                    <Streams />
                    {this.SideMenubar()}
                </Beforeunload>
                </div>
            )
        }
        if (this.state.StreamDiv) {
            return (
                <div>
                <Beforeunload onBeforeunload={()=>localStorage.removeItem("token")}>
                    <BooksAdmin />
                    {this.SideMenubar()}
                </Beforeunload>
                </div>
            );
        }
        if (this.state.FeedbackDiv) {
            return (
                <div>
                <Beforeunload onBeforeunload={()=>localStorage.removeItem("token")}>
                    <FeedbackAdmin />
                    {this.SideMenubar()}
                </Beforeunload>
                </div>
            );
        }
        return (
            <Beforeunload onBeforeunload={()=>localStorage.removeItem("token")}>
            <div className="container-fluid">
                <div className="row heading-div">
                    <div>
                        <h2 className="headingBrand">
                            Notebook
                    </h2>
                    </div>
                </div>
                <div className="row">
                    <div className="col-10">
                        <h1>
                            Wellcome Admin
                    </h1>
                    </div>
                </div>
                {this.SideMenubar()}
            </div>
            </Beforeunload>
        )
    }
}
