import React, { Component } from "react";
import { Link } from "react-router-dom";
import axios from "axios";
const Exercise = props => (
    <tr>
        <td>
            hello
        </td>
    </tr>
    // <tr>
    //     <td className="tbody-light">{props.exercise.username}</td>
    //     <td className="tbody-light">{props.exercise.description}</td>
    //     <td className="tbody-light">{props.exercise.duration}</td>
    //     <td className="tbody-light">{props.exercise.date.subString(0, 10)}</td>
    //     <td className="tbody-light">
    //         <Link to={"/edit/"+props.exercise._id}>Edit</Link>
    //         <a href="#" onClick={props.deleteExcercise(props.exercise._id)}>Delete</a>
    //     </td>
    // </tr>
);
export default class ExerciseList extends Component {
    constructor(props) {
        super(props);
        this.deleteExcercise = this.deleteExcercise.bind(this);
        this.state = {
            exercises: []
        }
    }
    componentDidMount() {
        axios.get("http://localhost:5000/exercise/").then(res => {
                this.setState({
                    exercises:res.data.map(val => val.username)
                })
                console.log(this.state.ecercises)
        })
    }

    deleteExcercise(id) {
        axios.delete("http://localhost:5000/exercise/" + id).then(res => console.log(res.data));
        this.setState({
            exercises: this.state.exercises.filter(el => el._id !== id)
        });
    }
    exerciseList() {
        return this.state.exercises.map(currentExercise => {
            return <Exercise key={currentExercise._id} exercise={currentExercise} deleteExercise={this.deleteExcercise} />
        })
    }
    render() {
        return (
            <div>
                <h1>LoggedExercise</h1>
                <table className="table table-bordered table-hover">
                    <thead className="thead-dark">
                        <tr>
                            <th>Username</th>
                            <th>Description</th>
                            <th>Duration</th>
                            <th>Date</th>
                            <th>Action</th>
                        </tr>
                    </thead>
                    <tbody>
                        <Exercise />
                    </tbody>
                </table>
            </div>
        )
    }
}
