import React, { Component } from "react";
import DatePicker from "react-datepicker";
import "react-datepicker/dist/react-datepicker.css"
export default class CreateExercise extends Component {
    constructor(props) {
        super(props);
        this.onChangeUsername = this.onChangeUsername.bind(this);
        this.onChangeDescription = this.onChangeDescription.bind(this);
        this.onChangeDuration = this.onChangeDuration.bind(this);
        this.onChangeDate = this.onChangeDate.bind(this);
        this.onSubmitForm= this.onSubmitForm.bind(this);
        this.state = {
            username: "",
            description: "",
            duration: 0,
            data: new Date(),
            users: []
        }
    }
    componentDidMount() {
        this.setState({
            users: ["test user"],
            username: "test user"
        })
    }
    onChangeUsername(event) {
        const username = event.target.value;
        this.setState({
            username: username,
        })
    }
    onChangeDescription(event) {
        const description = event.target.value;
        this.setState({
            description: description,
        })
    }
    onChangeDuration(event) {
        const duration = event.target.value;
        this.setState({
            duration: duration,
        })
    }
    onChangeDate(event) {
        const date = event.target.value;
        this.setState({
            date: date,
        })
    }
    onSubmitForm(event) {
        event.preventDefault();
        const exercise = {
            username: this.state.username,
            description: this.state.description,
            duration: this.state.duration,
            date: this.state.date,
        }
        console.log(exercise);
        window.location("/");
    }
    render() {
        return (
                <form onSubmit={this.onSubmitForm}>
                    <div class="form-group">
                        <label for="exampleFormControlInput1">user name</label>
                        <select id="inputState" onChange={this.onChangeUsername} class="form-control">
                            {
                                this.state.users.map(user => {
                                    return <option key={user} value={user}>{user}

                                    </option>
                                })
                            }
                        </select>
                        <label for="exampleFormControlInput1">description</label>
                        <textarea class="form-control" value={this.state.description} onChange={this.onChangeDescription} id="exampleFormControlTextarea1" rows="3"></textarea>
                        <label for="exampleFormControlInput1" >duration</label>
                        <input type="text" class="form-control" value={this.state.duration} onChange={this.onChangeDuration} id="exampleFormControlInput1" />
                        <label for="exampleFormControlInput1" >Pick Date Below</label>
                        <input type="date" class="form-control" onChange={this.onChangeDate} value={this.state.date}/>
                        <button type="submit" class="btn btn-primary mb-2">submit</button>
                    </div>
                </form>
        )
    }
}
