import axios from "axios";

export default class BooksCategory extends Component {
    constructor(props){
        super(props);
        this.state ={
            books:[],
            Goto:""
        }
    }

    componentDidMount() {
        axios.get("http://localhost:5000/user/").then(res => {
            console.log(res.data)    
        if (res.data.length > 0) {
                this.setState({
                    books: res.data.map(val => val.username),
                })
            }
        })
    }
