import React, { Component } from "react";
import axios from "axios";
export default class CreateExercise extends Component {
    constructor(props) {
        super(props);
        this.onChangeUsername = this.onChangeUsername.bind(this);
        this.onChangeDescription = this.onChangeDescription.bind(this);
        this.onChangeDuration = this.onChangeDuration.bind(this);
        this.onChangeDate = this.onChangeDate.bind(this);
        this.onSubmitForm = this.onSubmitForm.bind(this);
        this.state = {
            username: "",
            description: "",
            duration: 0,
            file:null,
            data: new Date(),
            users: []
        }
    }
    componentDidMount() {
        axios.get("http://localhost:5000/user/").then(res => {
            if (res.data.length > 0) {
                this.setState({
                    users:res.data.map(val => val.username),
                    username:res.data[0].username
                })
            }
        })
    }
    onChangeUsername(event) {
        const username = event.target.value;
        this.setState({
            username: username,
        })
    }
    onChangeDescription(event) {
        const description = event.target.value;
        this.setState({
            description: description,
        })
    }
    onChangeDuration(event) {
        const duration = event.target.value;
        this.setState({
            duration: duration,
        })
    }
    onChangeDate(event) {
        const date = event.target.value;
        this.setState({
            date: date,
        })
    }
    fileChangedHandler = (event) => {
        const file = event.target.files[0]
      }
      
    onSubmitForm(event) {
        event.preventDefault();
        const exercise = {
            username: this.state.username,
            description: this.state.description,
            duration: this.state.duration,
            date: this.state.date,
        }
        console.log(exercise);
        axios.post("http://localhost:5000/exercise/add", exercise).then(res => console.log(res.data));
        window.location = "/";
    }
    render() {
        return (
            <form onSubmit={this.onSubmitForm}>
                <div className="form-group">
                    <label htmlFor="exampleFormControlInput1">user name</label>
                    <select id="inputState" onChange={this.onChangeUsername} className="form-control">
                        {
                            this.state.users.map(user => {
                                return <option key={user} value={user}>{user}

                                </option>
                            })
                        }
                    </select>
                    <label htmlFor="exampleFormControlInput1">description</label>
                    <textarea className="form-control" value={this.state.description} onChange={this.onChangeDescription} id="exampleFormControlTextarea1" rows="3"></textarea>
                    <label htmlFor="exampleFormControlInput1" >duration</label>
                    <input type="text" className="form-control" value={this.state.duration} onChange={this.onChangeDuration} id="exampleFormControlInput1" />
                    <label htmlFor="exampleFormControlInput1" >Pick Date Below</label>
                    <input type="date" className="form-control" onChange={this.onChangeDate} value={this.state.date} />
                    <label htmlFor="exampleFormControlInput1" >Pick File</label>
                    <input type="file" className="form-control" onChange={this.fileChangedHandler}/>
                    <button style={{ "marginTop": "10px" }} type="submit" className="btn btn-primary mb-2">submit</button>
                </div>
            </form>
        )
    }
}
