import React, { Component } from "react";
import "bootstrap/dist/css/bootstrap.min.css";
import axios from "axios";
import "../component/css/AdminDashboard.css"
import { Link } from "react-router-dom";
import { Redirect } from "react-router-dom";
import Streams from "../component/streams";
import FeedbackAdmin from "../component/FeedbackAdmin";

import BooksAdmin from "../component/BooksAdmin";
import { Beforeunload } from 'react-beforeunload';

export default class AdminDashboard extends Component {
    constructor(props) {
        super(props);
        this.ExerciseFunction = this.ExerciseFunction.bind(this);
        this.StreamsFunction = this.StreamsFunction.bind(this);
        this.SideMenubar = this.SideMenubar.bind(this);
        const token = localStorage.getItem("token");
        let LoggedIn = true;
        if (token === null) {
            LoggedIn = false;
        }
        this.state = {
            exerciseDiv: false,
            StreamDiv: false,
            FeedbackDiv:false,
            LoggedIn
        }
    }
    StreamsFunction() {
        this.setState({
            StreamDiv: true,
            exerciseDiv: false
        })
    }
    ExerciseFunction() {
        console.log("button is");
        this.setState({
            exerciseDiv: true,
            StreamDiv: false
        });
    }
    SideMenubar(){
        return (
            <div className="col-2 borderOfOptions sidenav">
                        <ul className="ul-padding-none">
                            <Link className="LinksForOptions" onClick={this.ExerciseFunction}>
                                <li>streams</li>
                            </Link>
                            <Link className="LinksForOptions" onClick={this.StreamsFunction}>
                                <li>Books</li>
                            </Link>
                            <Link className="LinksForOptions" onClick={""}>
                                <li>Feedback</li>
                            </Link>
                            <Link className="LinksForOptions" to="/Logout">
                                <li><button className="btn btn-dark btn-block ">Logout</button></li>
                            </Link>
                        </ul>
                    </div>
        )
    }
    render() {
        if (this.state.LoggedIn === false) {
            return  <Redirect to="/" />
        }
        if (this.state.exerciseDiv) {
            return (
                <div>
                  <Beforeunload onBeforeunload={()=>localStorage.removeItem("token")}>
                    <Streams />
                    {this.SideMenubar()}
                </Beforeunload>
                </div>
            )
        }
        if (this.state.StreamDiv) {
            return (
                <div>
                <Beforeunload onBeforeunload={()=>localStorage.removeItem("token")}>
                    <BooksAdmin />
                    {this.SideMenubar()}
                </Beforeunload>
                </div>
            );
        }
        if (this.state.FeedbackDiv) {
            return (
                <div>
                <Beforeunload onBeforeunload={()=>localStorage.removeItem("token")}>
                    <FeedbackAdmin />
                    {this.SideMenubar()}
                </Beforeunload>
                </div>
            );
        }
        return (
            <Beforeunload onBeforeunload={()=>localStorage.removeItem("token")}>
            <div className="container-fluid">
                <div className="row heading-div">
                    <div>
                        <h2 className="headingBrand">
                            Notebook
                    </h2>
                    </div>
                </div>
                <div className="row">
                    <div className="col-10">
                        <h1>
                            Wellcome Admin
                    </h1>
                    </div>
                </div>
                {this.SideMenubar()}
            </div>
            </Beforeunload>
        )
    }
}
