import React, { Component } from "react";
import "bootstrap/dist/css/bootstrap.min.css";
import "./css/BooksCategory.css";
import { Link } from "react-router-dom";
import axios from "axios";
const Stream= props=>{
    return (
        <div>
            <div className="col-5 col-div-books-category">
                <p>{props.streamName}</p>
                    <Link to="/">
                     <button className="btn btn-dark-gray btn-primary">
                     Get Books
                     </button>
                    </Link>
        </div>
        </div>
    )
}
export default class BooksCategory extends Component {
    constructor(props){
        super(props);
        this.BooksList= this.BooksList.bind(this);
        this.state ={
            books:[],
            Goto:""
        }
    }

    componentDidMount() {
        axios.get("http://localhost:5000/user/").then(res => {
        if (res.data.length > 0) {
                this.setState({
                    books: res.data.map(val => val.username),
                })
            }
        })
     }
     BooksList(props){
        return this.state.books.map(currentExercise => {
            return currentExercise;
            // return <Stream key={currentExercise._id} id={currentExercise._id} streamName={currentExercise.username}/>
        })
     }
    
    render() {
        return (
            <div className="container-fluid">
                {this.BooksList}
            </div>
        )

    }
}
