import React, { Component } from "react";
import "bootstrap/dist/css/bootstrap.min.css";
import axios from "axios";
import { Button, Modal } from 'react-bootstrap';

function MyVerticallyCenteredModal(props) {
    const [pass, setPass] = React.useState("");
    

    function  onChangePassword(event){
        console.log(event.target.value);
        const passWord = event.target.value;
        setPass(passWord);
    }
    function HandleFormSubmission(event){
        event.preventDefault();
        console.log("button is clicked.");
        const password = pass;
        console.log(password);
        axios
        .post("http://localhost:5000/password", {passToPost:password})
        .then(res => AdminDashBoard(res))
        .catch(err => console.log(err));
    }
    function AdminDashBoard(res){
      console.log(res.data.authentication);
      if(res.data.authentication === true){
        localStorage.setItem("token", "logged in!");
      }
    }
    return (
      <Modal
        {...props}
        size="lg"
        aria-labelledby="contained-modal-title-vcenter"
        centered
      >
        <Modal.Header closeButton>
          <Modal.Title id="contained-modal-title-vcenter">
            Please enter password provided by developer.
          </Modal.Title>
        </Modal.Header>
        <Modal.Body>
          <h4>Enter password</h4>
          <form method="POST" onSubmit={HandleFormSubmission}>
            <div class="form-group">
                <p>{pass}</p>
              <input type="password"  name="pass"  onChange={onChangePassword} class="form-control" min="8"/>
            </div>
            <button name="AdminLoginButton" className="btn btn-dark"  type="submit" >Submit</button>
          </form>
        </Modal.Body>
        <Modal.Footer>
          <Button  className="btn btn-dark" onClick={props.onHide} >Close</Button>
        </Modal.Footer>
      </Modal>
    );
  }
  
  function AdminLoginPage(){
    const [modalShow, setModalShow] = React.useState(false);
  
    return (
        <>
        <Button variant="dark" onClick={() => setModalShow(true)}>
          Admin
        </Button>
        <MyVerticallyCenteredModal
          show={modalShow}
          onHide={() => setModalShow(false)}
        />
      </>
    );
}
// export default AdminLogin; 

export  default class AdminLogin extends Component {
  constructor(props){

  }
  render(){
      return (
        <div>
          
        </div>
      )
  }
}
