import React, { Component } from "react";
import { Link } from "react-router-dom";
import axios from "axios";
const Exercise = props => (
    <tr>
          <td className="tbody-light">{props!== undefined?props.exercise.username:""}</td>
         <td className="tbody-light">{props !== undefined?props.exercise.description:""}</td>
         <td className="tbody-light">{props !== undefined?props.exercise.duration:""}</td>
         <td className="tbody-light">{props !== undefined?props.exercise.date.subString(0, 10):""}</td> 
         <td className="tbody-light">
            <Link to={"/edit/"+props.exercise._id}>Edit</Link> 
             <a href="#" onClick={props.exercise._id?props.deleteExcercise(props.exercise._id):""}>Delete</a> 
         </td>
    </tr>
);
export default class ExerciseList extends Component {
    constructor(props) {
        super(props);
        this.deleteExcercise = this.deleteExcercise.bind(this);
        this.state = {
            exercises: [],
        }
    }
    CheckReload = (() => {
        let counter = - 5;
        return () => {
          counter++;
          return counter;
        };
      })();
    componentDidMount() {
        axios.get("http://localhost:5000/user/").then(res => {
            this.setState({
                exercises: res.data
            });
        }).catch(err => console.log(err));
        console.log("thi is state");
        console.log(this.state);

      const   refreshId = setInterval(
            () => {
              const properID = CheckReload();
              console.log(properID);
              if (properID > 0) {
                clearInterval(refreshId);
              }
            },
            1000
          );
    }  

    deleteExcercise(id) {
        axios.delete("http://localhost:5000/exercise/" + id).then(res => console.log(res.data));
        this.setState({
            exercises: this.state.exercises.filter(el => el._id !== id)
        });
        console.log(this.state);
    }
    exerciseList() {
        return this.state.exercises.map(currentExercise => {
            return <Exercise key={currentExercise._id} exercise={currentExercise} deleteExercise={this.deleteExcercise} />
        })
    }
    render() {
        return (
            <div>
                <h1>LoggedExercise</h1>
                <table className="table table-bordered table-hover">
                    <thead className="thead-dark">
                        <tr>
                            <th>Username</th>
                            <th>Description</th>
                            <th>Duration</th>
                            <th>Date</th>
                            <th>Action</th>
                        </tr>
                    </thead>
                    <tbody>
                        <Exercise />
                    </tbody>
                </table>
            </div>
        )
    }
}
