import React, { Component } from "react";
import "bootstrap/dist/css/bootstrap.min.css";
import axios from "axios";
import { Button, Modal } from 'react-bootstrap';
import {Redirect} from "react-router-dom";

class MyVerticallyCenteredModal extends Component{
    constructor(props){
      super(props);
      this.onChangePassword = this.onChangePassword.bind(this);
      this.HandleFormSubmission = this.HandleFormSubmission.bind(this);
      this.AdminDashBoard = this.AdminDashBoard.bind(this);
      this.state={
        pass:""
      }
    }

    onChangePassword(event){
        console.log(event.target.value);
        const passWord = event.target.value;
        setPass(passWord);
    }
    HandleFormSubmission(event){
        // event.preventDefault();
        console.log("button is clicked.");
        const password = pass;
        console.log(password);
        axios
        .post("http://localhost:5000/password", {passToPost:password})
        .then(res => AdminDashBoard(res))
        .catch(err => console.log(err));
    }
    AdminDashBoard(res){
      if(res.data.authentication === true){
        console.log(res.data.authentication);
        // localStorage.setItem("token", "logged in!");
        return <Redirect to={"/AdminDashboard"} />
      }
    }
    render(){

    
    return (
      <Modal
        {...props}
        size="lg"
        aria-labelledby="contained-modal-title-vcenter"
        centered
      >
        <Modal.Header closeButton>
          <Modal.Title id="contained-modal-title-vcenter">
            Please enter password provided by developer.
          </Modal.Title>
        </Modal.Header>
        <Modal.Body>
          <h4>Enter password</h4>
          <form method="POST" onSubmit={HandleFormSubmission}>
            <div class="form-group">
                <p>{pass}</p>
              <input type="password"  name="pass"  onChange={onChangePassword} class="form-control" min="8"/>
            </div>
            <button name="AdminLoginButton" className="btn btn-dark"  type="submit" >Submit</button>
          </form>
        </Modal.Body>
        <Modal.Footer>
          <Button  className="btn btn-dark" onClick={props.onHide} >Close</Button>
        </Modal.Footer>
      </Modal>
    );
  }
}
  function AdminLogin(){
    const [modalShow, setModalShow] = React.useState(false);
  
    return (
        <>
        <Button variant="dark" onClick={() => setModalShow(true)}>
          Admin
        </Button>
        <MyVerticallyCenteredModal
          show={modalShow}
          onHide={() => setModalShow(false)}
        />
      </>
    );
}
export default AdminLogin;
