import React, { Component } from "react";
import "bootstrap/dist/css/bootstrap.min.css";
import "./css/BooksCategory.css";
import { Link } from "react-router-dom";
import axios from "axios";

const bookForDownload= props=>{
    return (
            <div className="col-5 col-div-books-category">
                <p>{props.streamName}</p>
                    <Link to={"/books/"+props.streamName}>
                     <button className="btn btn-dark-gray btn-primary">
                     Get Books
                     </button>
                    </Link>
        </div>
    )
}

export default class books extends Component {
    constructor(props){
        super(props);
        this.componentDidMount= this.componentDidMount.bind(this)
        this.printData = this.printData.bind(this);
        this.state = {
            data :[],
        }
    }
    componentDidMount(){
        axios.get("http://localhost:5000/exercise/").then(res => {
            console.log(this.props.match.params.name);
            this.setState({
                data:res.data.users
            })
    });
}
printData(){
    console.log(this.state.data);
}
    render() {
        return (
            <div className="row row-div-Books-category">
                {this.printData()}
                <div className="col-5 col-div-books-category">
                <div class="card" style="width: 18rem;">
  <img class="card-img-top" src=".../100px180/?text=Image cap" alt="Card image cap" />
  <div class="card-body">
    <h5 class="card-title">Card title</h5>
    <p class="card-text">Some quick example text to build on the card title and make up the bulk of the card's content.</p>
  </div>
  <ul class="list-group list-group-flush">
    <li class="list-group-item">Cras justo odio</li>
    <li class="list-group-item">Dapibus ac facilisis in</li>
    <li class="list-group-item">Vestibulum at eros</li>
  </ul>
  <div class="card-body">
    <a href="#" class="card-link">Card link</a>
    <a href="#" class="card-link">Another link</a>
  </div>
</div>                </div>
            </div>
        )

    }
}
