import React, { Component } from "react";
import "bootstrap/dist/css/bootstrap.min.css";
import axios from "axios";
import "../component/css/AdminDashboard.css"
import { Link } from "react-router-dom";

const Exercise = props => (
    <tr className="col">
        <td>
            <img height="100px" width="100px" src={"http://localhost:5000/exercise/image/:" + (props.file).substring(15)} alt={(props.file).substring(15)} />
            <a href={"http://localhost:5000/exercise/pdf/:" + (props.pdfFile).substring(15)} rel="noopener noreferrer" target="_blank" >Link</a>
        </td>
        <td className="tbody-light">{props.exercise.username}</td>
        <td className="tbody-light">{props.exercise.description}</td>
        <td className="tbody-light">{props.exercise.duration}</td>
        <td className="tbody-light">{(props.exercise.date).substring(0, 10)}</td>
        {/* <td className="tbody-light">{props.exercise.path} */}
        {/* </td> */}
        <td className="tbody-light">
            <Link to={"/EditExercise/" + props.exercise._id}>
                <button style={{ margin: "5px" }} type="button" className="btn btn-primary btn-md">Edit</button>
            </Link>
        </td>
        <td>
            <Link to="/" className="button-small" onClick={() => props.deleteExercise(props.id)}>
                <button type="button" className="btn btn-primary btn-md"> Delete </button>
            </Link>
        </td>
    </tr>
);
const StreamElementCreator = props =>(
<tr className="col">
        <td className="tbody-light">{props.username}</td>
        <td className="tbody-light">
            <Link to={""}>
                <button style={{ margin: "5px" }} type="button" className="btn btn-primary btn-md">Delete</button>
            </Link>
        </td>
        
</tr>
);
export default class AdminDashboard extends Component {
    constructor(props) {
        super(props);
        this.deleteExcercise = this.deleteExcercise.bind(this);
        this.Hello = this.Hello.bind(this);
        this.hello = this.hello.bind(this);
        this.componentDidMount = this.componentDidMount.bind(this);
        this.state = {
            exercises: [],
            exerciseDiv:false,
            streams:[]
        }
    }
    componentDidMount() {
        axios.get("http://localhost:5000/exercise/").then(res => {
            console.log(res);
            this.setState({
                exercises: res.data.users,
            });
            // console.log(res.data);
            // console.log(this.state);
        }).catch(err => console.log(err));
        axios.get("http://localhost:5000/user/").then(res => {
            console.log(res);
            this.setState({
                streams: res.data,
            });
        }).catch(err => console.log(err));
    }
    deleteExcercise(id) {
        axios.delete("http://localhost:5000/exercise/" + id).then(res => console.log(res.data));
        this.setState({
            exercises: this.state.exercises.filter(el => el._id !== id)
        });
        console.log(this.state);
    }
    Hello() {
        console.log("button is");
        this.setState({
            exerciseDiv:true
        });
    }
     exerciseList() {
        return this.state.exercises.map(currentExercise => {
            return <Exercise file={currentExercise.path} pdfFile={currentExercise.pathPdf} key={currentExercise._id} id={currentExercise._id} exercise={currentExercise} deleteExercise={this.deleteExcercise} />
        })
    }
    Streams() {
        console.log(this.state.streams);
        return this.state.streams.map(currentExercise => {
            return <StreamElementCreator username={currentExercise.username} key={currentExercise._id} id={currentExercise._id} deletestream={this.deleteExcercise} />
        });
    }
  
    hello(){
        console.log();
    }
   
    render() {
        if(this.state.exerciseDiv){
            return (
                <div className="container-fluid">
            <div className="row heading-div">
                <div>
                    <h2 className="headingBrand">
                    Notebook
                    </h2>
                </div>
                {this.Streams()}
                    <div className="col-2 borderOfOptions sidenav">
                        <ul>
                           <Link className="LinksForOptions" onClick={this.Hello}>
                                <li>streams</li>
                            </Link>
                           <Link className="LinksForOptions" onClick={this.hello}>
                                <li>Books</li>
                            </Link> 
                            <li>
                            <button onClick={this.Hello}>button</button>
                            </li>
                        </ul>
                    </div>
                </div>
            </div>
            
            )
        }
        return ( 
            <>
            <div className="container-fluid">
            <div className="row heading-div">
                <div>
                    <h2 className="headingBrand">
                    Notebook
                    </h2>
                </div>
            </div>
                <div className="row">
                    <div className="table-responsive-xl col-10" id="list">
                        <table style={{ textAlign: "center" }}  className="table table-bordered table-striped">
                            <thead className="thead-dark">
                                <tr>
                                    <th>Profile</th>
                                    <th>Username</th>
                                    <th>Description</th>
                                    <th>Duration</th>
                                    <th>Date</th>
                                    <th>File</th>
                                    <th colSpan="2" >Action</th>
                                </tr>
                            </thead>
                            <tbody>
                                {this.exerciseList()}
                            </tbody>
                        </table>
                    </div>
                    <div className="col-2 borderOfOptions sidenav">
                        <ul>
                           <Link className="LinksForOptions" onClick={this.Hello}>
                                <li>streams</li>
                            </Link>
                           <Link className="LinksForOptions" onClick={this.hello}>
                                <li>Books</li>
                            </Link> 
                            <li>
                            <button onClick={this.Hello}>button</button>
                            </li>
                        </ul>
                    </div>
                </div>
            </div>
            </>
        )
    }
}
