import React, { Component } from "react";
import { Link } from "react-router-dom";
import axios from "axios";
const Exercise = props => (
    <tr>
        <td className="tbody-light"></td>
        <td className="tbody-light"></td>
        <td className="tbody-light"></td>
        <td className="tbody-light"></td>
        <td className="tbody-light"></td>
    </tr>
);
export default class ExerciseList extends Component {
    constructor(props) {
        super(props);
        this.deleteExcercise = this.deleteExcercise.bind(this);
        this.state = {
            exercises: []
        }
    }
    componentDidMount() {
        axios.get("http://localhost:5000/exercise/").then(res => {
            this.setState({ exercises: res.data });
        }
        ).catch(err => console.log(err));
    }
    deleteExcercise(id) {
        axios.delete("http://localhost:5000/exercise/delete/:" + id).then(res => console.log(res.data));
        this.setState({
            exercises: this.state.exercises.filter(el => el._id !== id)
        });
    }
    exerciseList() {
        return this.state.exercises.map(currentExercise => {
            return <Exercise key={currentExercise._id} exercise={currentExercise} deleteExercise={this.deleteExcercise} />
        })
    }
    render() {
        return (
            <div>
                <h1>LoggedExercise</h1>
                <table className="table table-bordered table-hover">
                    <thead className="thead-dark">
                        <tr>
                            <th>Username</th>
                            <th>Description</th>
                            <th>Duration</th>
                            <th>Date</th>
                            <th>Action</th>
                        </tr>
                    </thead>
                    <tbody>

                    </tbody>
                </table>
            </div>
        )
    }
}
